test.config:
indexer
{
	mem_limit = 16M
}

searchd
{

}

source test1
{
	type = mysql

	sql_query = SELECT * FROM test_table where document_id in ( 1, 2 )
}

index test
{
	source	= test1
	path	= /test
}

index test_crc
{
	source	= test1
	path	= /crc

	dict			= crc
	min_prefix_len	= 1
	enable_star		= 1
}

index test_kw
{
	source	= test1
	path	= /kw

	dict			= keywords
	min_prefix_len	= 1
	enable_star		= 1
}

source test2
{
	type = mysql

	sql_query = SELECT * FROM test_table where document_id in ( 10, 11 )
}

index crc2
{
	source	= test2
	path	= /crc2

	dict			= crc
	min_infix_len	= 3
	enable_star		= 1
}
index kw2
{
	source	= test2
	path	= /kw2

	dict			= keywords
	min_infix_len	= 3
	enable_star		= 1
}

test.db_create:
CREATE TABLE test_table
(
	document_id INT NOT NULL,
	text VARCHAR(255) NOT NULL
