test.config:
indexer
{
	mem_limit = 16M
}

searchd
{

}

source test
{
	type = mysql

	sql_query = SELECT * FROM test_table
	sql_attr_uint	= idd
	sql_file_field	= body
}

index test
{
	source			= test
	path			= /test
	docinfo			= extern

	html_strip		= 1	
	index_sp		= 1
	index_zones		= zone*
}

source start_end
{
	type = mysql

	sql_query = SELECT id, body, 1 as idd FROM start_end_table
	sql_attr_uint	= idd
	sql_field_string	= body
}

index start_end
{
	source			= start_end
	path			= /start_end
	docinfo			= extern
}

source src_1
{
	type = mysql

	sql_query = SELECT 1 as id, 'Footpath' as title, 'Republic of Ireland' as country
	sql_field_string	= title
	sql_field_string	= country
}

index i1
{
	source			= src_1
	path			= /i1
	docinfo			= extern
	min_word_len = 1
	morphology = stem_en
	index_exact_words = 1
}

source src_fl
{
	type = mysql

	sql_query = SELECT 1 as id, 'we are from here' as title, 'but live there all the time' as body UNION SELECT 2 as id, 'but live there all the time' as title, 'we are from here' as body
	sql_field_string	= title
	sql_field_string	= body
}

index fl
{
	source			= src_fl
	path			= /fl
	docinfo			= extern
}

test.db_create:
CREATE TABLE test_table
(
	id INT NOT NULL,
	body VARCHAR(2048) NOT NULL,
	idd INT NOT NULL
);

CREATE TABLE start_end_table
(
	id INTEGER PRIMARY KEY NOT NULL AUTO_INCREMENT,
	body VARCHAR(64) NOT NULL
);

test.custom_insert:
for ( $i=0; $i<98; ++$i )
	mysql_query ( "insert into start_end_table (body) values ('ABB')" );
mysql_query ( "insert into start_end_table (body) values ('ABB abc ABB')" );
for ( $i=0; $i<501; ++$i )
	mysql_query ( "insert into start_end_table (body) values ('ABB')" );
