test.config:
searchd
{



seamless_rotate = 1
seamless_rotate = 0

}

source test
{
	type				= mysql

	sql_query			= select * from test_table
	sql_attr_uint		= gid
	sql_field_string	= title
}

index test
{
	source				= test
	path				= /test
}

test.db_create:
CREATE TABLE test_table
(
	id INTEGER PRIMARY KEY NOT NULL,
	gid INTEGER NOT NULL,
	title VARCHAR(255) NOT NULL
);

test.custom_test:
$results = array();
$ql->Reconnect();
$extensions = array ( 'spa', 'spd', 'spe', 'sph', 'spi', 'spk', 'spm', 'spp', 'sps' );

$results[] = $ql->Query ( "SELECT * FROM test" );

foreach ( $extensions as $e )
	copy ( 'test_233/data/reload.' . $e, 'test_233/reload_path/reload.' . $e );
$results[] = $ql->Query ( "RELOAD INDEX test FROM 'test_233/reload_path/reload'" );
$results[] = $ql->Query ( "SELECT * FROM test" );

foreach ( $extensions as $e )
	copy ( 'test_233/data/test.' . $e, 'data/test.new.' . $e );
$results[] = $ql->Query ( "RELOAD INDEX test" );
$results[] = $ql->Query ( "SELECT * FROM test" );
